import React from "react";

class Hero extends React.Component {
  logo = "https://cdn.auth0.com/blog/auth0-react-sample/assets/logo.png";

  render() {
    return (
      <div className="text-center hero">
        <img
          className="mb-3 app-logo"
          src={this.logo}
          alt="React logo"
          width="120"
        />
        <h1 className="mb-4">React Sample Project</h1>
        <p className="lead">
          This is a sample application that demonstrates an authentication flow
          for an SPA, using{" "}
          <a
            target="_blank"
            rel="noopener noreferrer"
            href="https://auth0.com/docs/quickstart/spa/react"
          >
            React
          </a>
        </p>
      </div>
    );
  }
}

export default Hero;
